// Función para filtrar vehículos según los filtros seleccionados
function filterVehicles() {
    const dlc = document.getElementById('dlc').checked;
    const reward = document.getElementById('reward').checked;
    const showroom = document.getElementById('showroom').checked;
    const division = document.getElementById('division').value;

    return vehicles.filter(vehicle => {
        return (!dlc || vehicle.dlc) &&
               (!reward || vehicle.reward) &&
               (!showroom || vehicle.showroom) &&
               (division === 'Todos' || vehicle.division === division);
    });
}

// Función para seleccionar un vehículo al azar
function getRandomVehicle() {
    const filteredVehicles = filterVehicles();
    const randomIndex = Math.floor(Math.random() * filteredVehicles.length);
    return filteredVehicles[randomIndex];
}

// Función para seleccionar una pista al azar
function getRandomTrack() {
    const randomIndex = Math.floor(Math.random() * tracks.length);
    return tracks[randomIndex];
}

// Función para mostrar el vehículo seleccionado (sin imagen)
function displayVehicle(vehicle) {
    // Eliminamos la línea que asigna la imagen porque no hay img
    // document.getElementById('vehicleImage').src = vehicle.image;
    document.getElementById('vehicleName').textContent = vehicle.name;
}

// Función para mostrar la pista seleccionada (sin imagen)
function displayTrack(track) {
    // Eliminamos la línea que asigna la imagen porque no hay img
    // document.getElementById('trackImage').src = track.image;
    document.getElementById('trackName').textContent = track.name;
}


// Event listeners para los botones
document.getElementById('vehicleBtn').addEventListener('click', () => {
    const vehicle = getRandomVehicle();
    displayVehicle(vehicle);
});

document.getElementById('trackBtn').addEventListener('click', () => {
    const track = getRandomTrack();
    displayTrack(track);
});
